<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Employer extends MX_Controller {	public function __construct() {		parent::__construct();		$this->load->library("session");		$this->load->helper('url');		$this->load->helper('common');		$this->load->library('form_validation');		$this->load->model("Employer_model");	}	// Get all Employe List	public function EmployerList(){		$data['user'] =getUser();		$data['County'] =getCounty();		$data['Category'] =getCategory();		$data['EmployerList'] =$this->Employer_model->getEmployers();		$data['JobType'] =jobType();		$data['main_content'] = 'employer-list';		$this->load->view('frontend/layout.php', $data);	}	// View Profile	public function viewProfile($id=Null){		$cid =base64_decode($id);		$data['user'] =getUser();		$data['EmployerProfile'] =$this->Employer_model->viewProfile($cid);		$data['main_content'] = 'employer-detail';		$this->load->view('frontend/layout.php', $data);	}	// send Invitation to Candidate	public function sendInvitation(){	$result = $this->Employer_model->sendInvitation();	echo $result;	}	// Invite User For Project	public function InviteUserForProject(){		$data['user'] =getUser();		$data['ResponseJobs'] =$this->Employer_model->InviteUserForProject();		$this->Employer_model->readInvitionProject();		$data['main_content'] = 'invite-user';		$this->load->view('frontend/frontend-admin-layout.php', $data);	}	// view Invite Particular Response Jobs user	public function viewInviteParticularResponseJobs($id=NULL){		$data['main_content'] = 'view-invite-response-job';		$data['user'] =getUser();		$data['ResponseJobs'] =$this->Employer_model->viewInviteParticularResponseJobs($id);		$data['checkAssignCount'] =$this->Employer_model->checkAssignCount($id);		$this->load->view('frontend/frontend-admin-layout.php', $data);	}			}