<?phpdefined('BASEPATH') OR exit('No direct script access allowed');	class Category extends MX_Controller {	public function __construct() {	parent::__construct();		$this->load->library("session");		$this->load->helper('url');		$this->load->library('form_validation');		$this->load->model("Category_model");	//$this->load->module("dashboard");	}	public function index(){		//echo "<pre>"; print_r("I am Here"); die;		if(!empty($_POST['login'])){			$this->session->set_userdata('name','test');				$this->form_validation->set_rules('email', 'Email','trim|valid_email|required');			$this->form_validation->set_rules('password', 'Password', 'required');			if($this->form_validation->run() === FALSE){				$this->load->view('index');			}else{				$result = $this->Administrator_model->admin_user();				//echo "<pre>"; print_r($result); die;					if($result==1){					redirect('administrator/dashboard');				}else{					$this->session->set_flashdata('danger', 'Invaild login credentials.Please try vaild credentials');					$this->load->view('index');				}			}					}else{			$this->load->view('index');		}	}	public function AddCategory(){		$data['user'] = $this->Administrator_model->getAdmin();		$data['title'] = 'Add Category';		$data['main_content'] = 'addcategory';		//$this->load->view('backend/backend-admin-layout.php',$data);		if(!empty($_POST['add_category'])){		  $this->form_validation->set_rules('category_name', 'Category Name', 'required');			if($this->form_validation->run() === FALSE){				$this->load->view('backend/backend-admin-layout.php', $data);			}else{				$result = $this->Administrator_model->AddCategory();				if($result==1){					$this->session->set_flashdata('success', 'Category has been added successfully.');				}else{					//$this->session->set_flashdata('danger', 'current password does not match with password');				}				redirect('add-category');				//$this->load->view('frontend/frontend-admin-layout.php', $data);			}					}		else{			$this->load->view('backend/backend-admin-layout.php',$data);		}		//echo "<pre>"; print_r("Here is Category"); die;	}	function logout() {		session_start(); 		session_destroy();		unset($_SESSION);		session_regenerate_id(true);		redirect('');	}	}